import * as $ from "jquery";
import {spfService} from "./js/tracking.js"

  
  

$(function(){

    var spfData = window.sdfOrderData;
    function checkId(id) {
 
        if(id ==='') {
            var error = new spfService();
                error.error(spfData.Warning,spfData.not_found,spfData.confirm_button);
                
            return false;
        }
        return true;
    }

            
    $(document).on('click','#print-tracking',function(e){
        var id = $(e.target).val();
        if(!checkId(id)) return;
        
            new spfService({spfData:spfData,params:{bill:id},target:e.target}).tracking();
    });

    $(document).on('click','#print-label',function(e) {
        var target = $(e.target);
        if(!checkId(target.val())) return;
        
        //defaultParams['type']   = spfData.printLabel;
        new spfService({spfData:spfData,params:{billID:target.val()},target:e.target}).printLabel();
        
    })

})
